const SEND_MESSAGE = 'SEND-MESSAGE';

let initialState = {
    isAuth: false
};

let appReducer = (state = initialState, action) => {
    switch (action.type) {
        case SEND_MESSAGE:
            let body = action.newMessageBody;
            return {
                ...state,
                messagesData: [...state.messagesData, {id: 6, message: body}]
            };
        default:
            return state
    }
};

export default appReducer;
