const AUTH = 'AUTH';

let initialState = {
    isAuth: false
};

let appReducer = (state = initialState, action) => {
    switch (action.type) {
        case AUTH:
            let body = action.auth;
            return {
                ...state,
                messagesData: localStorage.getItem()
            };
        default:
            return state
    }
};

export default appReducer;
