const AUTH = 'AUTH';

let initialState = {
    isAuth: false
};

let appReducer = (state = initialState, action) => {
    switch (action.type) {
        case AUTH:
            let body = action.auth;
            return {
                ...state,
                messagesData: [...state.messagesData, {id: 6, message: body}]
            };
        default:
            return state
    }
};

export default appReducer;
