const AUTH = 'AUTH';

let initialState = {
    isAuth: false
};

let appReducer = (state = initialState, action) => {
    switch (action.type) {
        case AUTH:
            return {
                ...state,
                isAuth: localStorage.getItem('id')
            };
        default:
            return state
    }
};



export default appReducer;
