const AUTH = 'AUTH';

let initialState = {
    isAuth: true
};

let appReducer = (state = initialState, action) => {
    switch (action.type) {
        case AUTH:
            return {
                ...state,
                isAuth: action.isAuth
            };
        default:
            return state
    }
};

export const setIsAuth = (isAuth) => {
    return {
        type: AUTH,
        isAuth
    }
};

export default appReducer;
