import React from "react";
import OrderShedule from "./OrderShedule/OrderShedule";
import './MainApp.css'
import filtersvg from "../../resources/icon-svg/filter.svg";
import {ReactSVG} from "react-svg";
import {Redirect} from "react-router";
import {connect} from "react-redux";
import {setIsAuth} from "../../state/app-reducer";
import profileicon from "../../resources/icon-svg/profile.svg";
import manageuser from "../../resources/icon-svg/manag-user.svg";
import LogoDronTaxi from "../../resources/icon-svg/LogoDronTaxi.svg";
import exit from "../../resources/icon-svg/exit.svg";

import OrdersContainer from "./OrdersContainer";
import Profile from "../profile/Profile";
import {NavLink} from "react-router-dom";

let MainApp = (props) => {
    return (props.isAuth !== 'false') ? <Redirect to="/signin"/> : (
        <div className="MainContainer">
            <div style={{flex: 1}}>
                <div style={{height: 10, display: "flex", alignItems: "center", paddingTop: 30, paddingLeft: 10}}>
                    <ReactSVG src={LogoDronTaxi}/>
                    <h3 style={{}}>DRON TAXI</h3>
                </div>
                <div className="LeftBar">
                    <div className="MenuElement">
                        <NavLink to="/profile">
                            <div style={{display: 'flex', alignItems: 'center'}}>
                                <ReactSVG src={profileicon}/>
                                <h6>Профиль</h6>
                            </div>
                        </NavLink>
                        <NavLink to="/orders">
                            <div style={{display: 'flex', alignItems: 'center'}}>
                                <ReactSVG src={manageuser}/>
                                <h6>Мои заказы</h6>
                            </div>
                        </NavLink>
                    </div>
                    <a style={{display:'flex', height: '100%', justifyContent: 'flex-end'}} onClick={() => localStorage.setItem('id', 'true')} href='/signin'>
                        <div style={{display: 'flex', alignItems: 'center'}}>
                            <ReactSVG src={exit}/>
                            <h6 style={{paddingLeft: 30}}>Выход</h6>
                        </div>
                    </a>
                </div>
            </div>
            <div style={{flex: 5}}>
                <OrdersContainer/>
                <Profile/>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => ({
    isAuth: state.appReducer.isAuth
})

export default connect(mapStateToProps, {setIsAuth})(MainApp);
