const AUTH = 'AUTH';

let ath;
(localStorage.getItem('id') == null) ? (ath = true) : ath = localStorage.getItem('id')

    let initialState = {
    isAuth: ath
};

let appReducer = (state = initialState, action) => {
    switch (action.type) {
        case AUTH:
            return {
                ...state,
                isAuth: action.isAuth
            };
        default:
            return state
    }
};

export const setIsAuth = (isAuth) => {
    return {
        type: AUTH,
        isAuth
    }
};

export default appReducer;
